document.addEventListener('DOMContentLoaded',()=>{ const toggle=document.querySelector('.nav-toggle'); const nav=document.getElementById('primary-nav'); toggle?.addEventListener('click',()=>{ const open=nav.classList.toggle('open'); toggle.setAttribute('aria-expanded',open?'true':'false'); }); document.querySelectorAll('form').forEach(f=>{ const hp=document.createElement('input'); hp.type='text'; hp.name='_gotcha'; hp.style.display='none'; f.appendChild(hp); }); });